mapper.select:
SELECT d.*, c.name as categoryName
FROM dish d LEFT JOIN category c ON d.category_id = c.id

     AND d.name LIKE CONCAT('%',#{name},'%')
    AND d.status = #{status}
    AND d.category_id = #{categoryId}

mapper.insert:
UPDATE dish
name = #{name}, price = #{price}, description = #{description}, image = #{image}, status = #{status}, category_id = #{categoryId}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id=#{id}
